import scala.collection.immutable.{TreeSet, TreeMap}

object Test extends App {

  def write(o: AnyRef): Array[Byte] = {
    val ba = new java.io.ByteArrayOutputStream(512)
    val out = new java.io.ObjectOutputStream(ba)
    out.writeObject(o)
    out.close()
    ba.toByteArray()
  }

  def read(buffer: Array[Byte]): AnyRef = {
    val in =
      new java.io.ObjectInputStream(new java.io.ByteArrayInputStream(buffer))
    in.readObject()
  }

  def parse(str: String) : Array[Byte] = {
    val b = Array.newBuilder[Byte]
    var i = 0
    while (i < str.length) {
      b += java.lang.Integer.parseInt(str.substring(i, i+2), 16).toByte
      i += 2
    }
    b.result()
  }

  def testRoundTrip(data: AnyRef) = {
    println(s" original - $data")
    println(s" original class - ${data.getClass}")
    val bytes = write(data)
    println(s" binary - ${bytes.map(b => f"$b%02X").mkString}")
    val recovered = read(bytes)
    println(s" recovered - $recovered")
    println(s" recovered class - ${recovered.getClass}")
    assert(data == recovered)
  }
  def testParse(data:AnyRef, bin: String): Unit = {
    val recovered = read(parse(bin))
    assert(data == recovered)
  }


  val data = List.tabulate(10)(i => s"key$i" -> s"value:$i")
  val treeMap: TreeMap[String, String] = (TreeMap.newBuilder[String, String] ++= data).result()
  val treeSet: TreeSet[String] = (TreeSet.newBuilder[String] ++= data.map(_._1)).result()

  testRoundTrip(treeMap)
  testRoundTrip(treeSet)

  //legacy format
  testParse(treeMap, "")
  testParse(treeSet, "")

  //new format
  testParse(treeMap, "")
  testParse(treeSet, "")

}